create table "public"."results" (
    "id" bigint generated by default as identity not null,
    "user_id" bigint,
    "job_link" text,
    "match_score" text,
    "created_at" timestamp with time zone not null default now(),
    "name" text,
    "email" text,
    "phone" text,
    "skills" text,
    "top_skill" text,
    "languages" text,
    "certifications" text,
    "improvements" text,
    "cover_letter" text
);


alter table "public"."results" enable row level security;

CREATE UNIQUE INDEX results_pkey ON public.results USING btree (id);

alter table "public"."results" add constraint "results_pkey" PRIMARY KEY using index "results_pkey";

alter table "public"."results" add constraint "results_user_id_fkey" FOREIGN KEY (user_id) REFERENCES users(id) not valid;

alter table "public"."results" validate constraint "results_user_id_fkey";

